import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import {
  Paper,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow
} from "@material-ui/core";
import { indigo } from "@material-ui/core/colors";
import PopupFilter from "./popupFilter.jsx";

const thColor = indigo[50];
const useStyles = makeStyles({
  base: {
    maxWidth: 900,
    margin: "auto"
  },
  table: {
    maxWidth: 500,
    margin: "40px auto 50px auto"
  },
  tableHead: {
    fontWeight: "bold",
    backgroundColor: thColor
  },
  textField: {
    margin: "30px 0 0 70px"
  },
  filterListIcon: {
    verticalAlign: "middle",
    marginLeft: "5px"
  }
});

function createData(thumb, name, brand, Contact, location) {
  return { thumb, name, brand, Contact, location };
}

const rows = [
  createData("", "Comfort Inn", "Choice", "Contact", <a href="https://www.choicehotels.com">Visit W3Schools</a>),
  createData("", "Quality Inn", "Choice", "Contact", "Oklahoma City, OK"),
  createData("", "3", "M", "東京", "信濃町"),
  createData("", "4", "K", "埼玉", "拝島"),
  createData("", "5", "N", "東京", "拝島")
];

const getColumns = rows => {
  return {
    thumb: Array.from(new Set(rows.map(row => row.thumb))),
    name: Array.from(new Set(rows.map(row => row.name))),
    brand: Array.from(new Set(rows.map(row => row.brand))),
    Contact: Array.from(new Set(rows.map(row => row.Contact))),
    location: Array.from(new Set(rows.map(row => row.location)))
  };
};

const filterRows = (obj, rows) => {
  const keys = Object.keys(obj);

  const filterRecursive = (keys, rows) => {
    if (keys.length === 0) return rows;

    if (obj[keys[0]].length === 0) {
      return filterRecursive(keys.slice(1), rows);
    }

    const filtered = rows.filter(row => {
      return obj[keys[0]].indexOf(row[keys[0]]) > -1;
    });

    return filterRecursive(keys.slice(1), filtered);
  };

  return filterRecursive(keys, rows);
};

export default function Content() {
  const classes = useStyles();
  const [checkedItems, setCheckedItems] = React.useState({
    thumb: [],
    name: [],
    brand: [],
    Contact: [],
    location: []
  });
  const [keywords, setKeywords] = React.useState({
    thumb: "",
    name: "",
    brand: "",
    Contact: "",
    location: ""
  });

  const filteredRows = filterRows(checkedItems, rows);
  const filteredColumns = getColumns(rows);

  return (
    <TableContainer component={Paper} className={classes.base}>
      <Table className={classes.table} aria-label="table-filterable">
        <TableHead className={classes.tableHead}>
          <TableRow>
             <TableCell align="center">

              <PopupFilter
                stateCheckedItems={[checkedItems, setCheckedItems]}
                stateKeywords={[keywords, setKeywords]}
                data={filteredColumns.thumb}
                columnName="thumb"
              />
            </TableCell>
            <TableCell align="center">
              NAME
              <PopupFilter
                stateCheckedItems={[checkedItems, setCheckedItems]}
                stateKeywords={[keywords, setKeywords]}
                data={filteredColumns.name}
                columnName="name"
              />
            </TableCell>
            <TableCell align="center">
              BRAND
              <PopupFilter
                stateCheckedItems={[checkedItems, setCheckedItems]}
                stateKeywords={[keywords, setKeywords]}
                data={filteredColumns.brand}
                columnName="brand"
              />
            </TableCell>
            <TableCell align="center">
              CONTACT
              <PopupFilter
                stateCheckedItems={[checkedItems, setCheckedItems]}
                stateKeywords={[keywords, setKeywords]}
                data={filteredColumns.Contact}
                columnName="Contact"
              />
            </TableCell>
            <TableCell align="center">
              LOCATION
              <PopupFilter
                stateCheckedItems={[checkedItems, setCheckedItems]}
                stateKeywords={[keywords, setKeywords]}
                data={filteredColumns.location}
                columnName="location"
              />
            </TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {filteredRows.map((row, index) => (
            <TableRow key={index}>
              <TableCell align="center">{row.thumb}</TableCell>
              <TableCell align="center">{row.name}</TableCell>
              <TableCell align="center">{row.brand} </TableCell>
              <TableCell align="center">{row.Contact}</TableCell>
              <TableCell align="center">{row.location}</TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
}
